mapper.insert:

insert into course_student (id, created_at, updated_at,
  course_id, student_id, lesson_count,
  registered_at, end_at, expense,
  discount, refund, remark
  )
values (#{id,jdbcType=INTEGER}, #{createdAt,jdbcType=TIMESTAMP}, #{updatedAt,jdbcType=TIMESTAMP},
  #{courseId,jdbcType=INTEGER}, #{studentId,jdbcType=INTEGER}, #{lessonCount,jdbcType=INTEGER},
  #{registeredAt,jdbcType=VARCHAR}, #{endAt,jdbcType=VARCHAR}, #{expense,jdbcType=INTEGER},
  #{discount,jdbcType=VARCHAR}, #{refund,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:
select student_id
from course_student
where course_id = #{courseId,jdbcType=INTEGER}

SELECT date_format(a.registered_at, '%x-%v') as period, SUM(a.expense) as income, sum(discount) as discount, sum(refund) as refund
FROM course_student AS a
GROUP BY period;

SELECT date_format(a.registered_at, '%Y-%m') as period, SUM(a.expense) as income, sum(discount) as discount, sum(refund) as refund
FROM course_student AS a
GROUP BY period;

SELECT date_format(a.registered_at, '%Y') as period, SUM(a.expense) as income, sum(discount) as discount, sum(refund) as refund
FROM course_student AS a
GROUP BY period;
